ed:@"xdh_fx_off_btn.png"] forKey:@"Highlighted"];	[imgDic2 setObject:[UIImage imageNamed:@"xdh_fx_on_btn.png"] forKey:@"Seleted"];	NSMutableDictionary *imgDic3 = [NSMutableDictionary dictionaryWithCapacity:3];	[imgDic3 setObject:[UIImage imageNamed:@"xdh_me_off_btn.png"] forKey:@"Default"];	[imgDic3 setObject:[UIImage imageNamed:@"xdh_me_off_btn.png"] forKey:@"Highlighted"];	[imgDic3 setObject:[UIImage imageNamed:@"xdh_me_on_btn.png"] forKey:@"Seleted"];		NSArray *images = @[imgDic,imgDic2,imgDic3];        _tabBarController = [[LeveyTabBarController alloc] initWithViewControllers:controllers imageArray:images];    //	[_tabBarController.tabBar setBackgroundImage:[UIImage imageNamed:@"tabbarbg.png"]];	[_tabBarController setTabBarTransparent:YES];        self.window.rootViewController = _tabBarController;    [self.window makeKeyAndVisible];    return YES;}- (void)navigationController:(UINavigationController *)navigationController willShowViewController:(UIViewController *)viewController animated:(BOOL)animated{	if ([viewController isKindOfClass:[LFHomeController class]])	{        [AppDelegate hidesTabBar:NO animated:YES];	}}- (void)applicationWillResignActive:(UIApplication *)application{    // Sent when the application is about to move from active to inactive state. This can occur for certain types of temporary interruptions (such as an incoming phone call or SMS message) or when the user quits the application and it begins the transition to the background state.    // Use this method to pause ongoing tasks, disable timers, and throttle down OpenGL ES frame rates. Games should use this method to pause the game.}- (void)applicationDidEnterBackground:(UIApplication *)application{    // Use this method to release shared resources, save user data, invalidate timers, and store enough application state information to restore your application to its current state in case it is terminated later.    // If your application supports background execution, this method is called instead of applicationWillTerminate: when the user quits.}- (void)applicationWillEnterForeground:(UIApplication *)application{    // Called as part of the transition from the background to the inactive state; here you can undo many of the changes made on entering the background.}- (void)applicationDidBecomeActive:(UIApplication *)application{    // Restart any tasks that were paused (or not yet started) while the application was inactive. If the application was previously in the background, optionally refresh the user interface.}- (void)applicationWillTerminate:(UIApplication *)application{    // Called when the application is about to terminate. Save data if appropriate. See also applicationDidEnterBackground:.}+ (AppDelegate *)sharedAppDelegate {    return (AppDelegate *)[UIApplication sharedApplication].delegate;}+ (void)hidesTabBar:(BOOL)hide animated:(BOOL)animated {    [[AppDelegate sharedAppDelegate].tabBarController hidesTabBar:hide animated:animated];}@end